import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Form, Button, Spinner, Alert, Badge, Modal } from 'react-bootstrap';
import axios from 'axios';
import { toast } from 'react-toastify';

const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [sortOrder, setSortOrder] = useState('desc');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [userStoreInfo, setUserStoreInfo] = useState(null);
  const [pagination, setPagination] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  
  const [createForm, setCreateForm] = useState({
    name: '',
    email: '',
    password: '',
    address: '',
    role: 'user'
  });

  const [formErrors, setFormErrors] = useState({});

  useEffect(() => {
    fetchUsers();
  }, [searchTerm, roleFilter, sortBy, sortOrder, currentPage]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        search: searchTerm,
        role: roleFilter,
        sortBy,
        sortOrder,
        page: currentPage,
        limit: '10'
      });
      
      const response = await axios.get(`/api/admin/users?${params}`);
      setUsers(response.data.data.users);
      setPagination(response.data.data.pagination);
    } catch (error) {
      console.error('Error fetching users:', error);
      toast.error(error.response?.data?.message || 'Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const fetchUserDetails = async (userId) => {
    try {
      const response = await axios.get(`/api/admin/users/${userId}`);
      setSelectedUser(response.data.data);
      
      // If user is store owner, fetch their store info
      if (response.data.data.role === 'store_owner') {
        try {
          const storeResponse = await axios.get(`/api/admin/stores?limit=100`);
          const userStore = storeResponse.data.data.stores.find(
            store => store.owner && store.owner._id === userId
          );
          setUserStoreInfo(userStore || null);
        } catch (err) {
          console.error('Error fetching store info:', err);
          setUserStoreInfo(null);
        }
      } else {
        setUserStoreInfo(null);
      }
      
      setShowDetailsModal(true);
    } catch (error) {
      console.error('Error fetching user details:', error);
      toast.error('Failed to load user details');
    }
  };

  const validateForm = () => {
    const errors = {};
    
    // Name validation
    if (!createForm.name || createForm.name.trim().length < 20) {
      errors.name = 'Name must be at least 20 characters';
    } else if (createForm.name.length > 60) {
      errors.name = 'Name cannot exceed 60 characters';
    }
    
    // Email validation
    if (!createForm.email || !/\S+@\S+\.\S+/.test(createForm.email)) {
      errors.email = 'Please provide a valid email address';
    }
    
    // Password validation
    if (!createForm.password || createForm.password.length < 8) {
      errors.password = 'Password must be at least 8 characters';
    } else if (createForm.password.length > 16) {
      errors.password = 'Password cannot exceed 16 characters';
    } else if (!/^(?=.*[A-Z])(?=.*[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]).+$/.test(createForm.password)) {
      errors.password = 'Password must contain at least one uppercase letter and one special character';
    }
    
    // Address validation
    if (!createForm.address || createForm.address.trim().length === 0) {
      errors.address = 'Address is required';
    } else if (createForm.address.length > 400) {
      errors.address = 'Address cannot exceed 400 characters';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      await axios.post('/api/admin/users', createForm);
      toast.success('User created successfully!');
      setShowCreateModal(false);
      setCreateForm({
        name: '',
        email: '',
        password: '',
        address: '',
        role: 'user'
      });
      setFormErrors({});
      fetchUsers();
    } catch (error) {
      console.error('Error creating user:', error);
      if (error.response?.data?.errors) {
        const backendErrors = {};
        error.response.data.errors.forEach(err => {
          backendErrors[err.path || err.param] = err.msg;
        });
        setFormErrors(backendErrors);
      }
      toast.error(error.response?.data?.message || 'Failed to create user');
    }
  };

  const toggleUserStatus = async (userId, currentStatus) => {
    if (!window.confirm(`Are you sure you want to ${currentStatus ? 'deactivate' : 'activate'} this user?`)) {
      return;
    }
    
    try {
      await axios.put(`/api/admin/users/${userId}`, {
        isActive: !currentStatus
      });
      toast.success(`User ${!currentStatus ? 'activated' : 'deactivated'} successfully!`);
      fetchUsers();
    } catch (error) {
      console.error('Error updating user:', error);
      toast.error('Failed to update user');
    }
  };

  const getRoleBadge = (role) => {
    const variants = {
      admin: 'danger',
      store_owner: 'warning',
      user: 'primary'
    };
    const labels = {
      admin: 'ADMIN',
      store_owner: 'STORE OWNER',
      user: 'USER'
    };
    return (
      <Badge bg={variants[role]}>
        {labels[role]}
      </Badge>
    );
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;
    
    for (let i = 0; i < 5; i++) {
      if (i < fullStars) {
        stars.push(<span key={i} className="text-warning"></span>);
      } else if (i === fullStars && hasHalfStar) {
        stars.push(<span key={i} className="text-warning"></span>);
      } else {
        stars.push(<span key={i} className="text-muted"></span>);
      }
    }
    return stars;
  };

  const clearFilters = () => {
    setSearchTerm('');
    setRoleFilter('');
    setSortBy('createdAt');
    setSortOrder('desc');
    setCurrentPage(1);
  };

  if (loading && users.length === 0) {
    return (
      <Container className="py-4">
        <div className="text-center">
          <Spinner animation="border" variant="primary" />
          <p className="mt-2">Loading users...</p>
        </div>
      </Container>
    );
  }

  return (
    <Container fluid className="py-4">
      <Row>
        <Col>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <h2 className="mb-0">User Management</h2>
              <p className="text-muted mb-0">Manage all users on the platform</p>
            </div>
            <Button variant="primary" onClick={() => setShowCreateModal(true)}>
              <i className="bi bi-plus-circle me-2"></i>
              Add New User
            </Button>
          </div>
        </Col>
      </Row>

      {/* Filters */}
      <Row className="mb-4">
        <Col>
          <Card className="border-0 shadow-sm">
            <Card.Body>
              <Row className="align-items-end">
                <Col md={3}>
                  <Form.Group>
                    <Form.Label className="small fw-bold">Search</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Search by name, email, or address..."
                      value={searchTerm}
                      onChange={(e) => {
                        setSearchTerm(e.target.value);
                        setCurrentPage(1);
                      }}
                    />
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="small fw-bold">Role</Form.Label>
                    <Form.Select
                      value={roleFilter}
                      onChange={(e) => {
                        setRoleFilter(e.target.value);
                        setCurrentPage(1);
                      }}
                    >
                      <option value="">All Roles</option>
                      <option value="admin">Admin</option>
                      <option value="user">User</option>
                      <option value="store_owner">Store Owner</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="small fw-bold">Sort By</Form.Label>
                    <Form.Select
                      value={sortBy}
                      onChange={(e) => setSortBy(e.target.value)}
                    >
                      <option value="createdAt">Date Created</option>
                      <option value="name">Name</option>
                      <option value="email">Email</option>
                      <option value="role">Role</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="small fw-bold">Order</Form.Label>
                    <Form.Select
                      value={sortOrder}
                      onChange={(e) => setSortOrder(e.target.value)}
                    >
                      <option value="desc">Descending</option>
                      <option value="asc">Ascending</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={3}>
                  <div className="d-flex gap-2">
                    <Button variant="outline-secondary" onClick={fetchUsers} disabled={loading}>
                      <i className="bi bi-arrow-clockwise"></i>
                    </Button>
                    <Button variant="outline-danger" onClick={clearFilters} className="flex-grow-1">
                      Clear Filters
                    </Button>
                  </div>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Users Table */}
      <Row>
        <Col>
          <Card className="border-0 shadow-sm">
            <Card.Header className="bg-primary text-white">
              <div className="d-flex justify-content-between align-items-center">
                <h5 className="mb-0">
                  Users ({pagination.totalUsers || 0})
                </h5>
                {loading && <Spinner animation="border" size="sm" />}
              </div>
            </Card.Header>
            <Card.Body className="p-0">
              {users.length === 0 ? (
                <Alert variant="info" className="m-4 text-center">
                  <i className="bi bi-info-circle me-2"></i>
                  No users found matching your criteria
                </Alert>
              ) : (
                <div className="table-responsive">
                  <Table hover className="mb-0">
                    <thead className="table-light">
                      <tr>
                        <th style={{ width: '25%' }}>Name</th>
                        <th style={{ width: '20%' }}>Email</th>
                        <th style={{ width: '25%' }}>Address</th>
                        <th style={{ width: '12%' }}>Role</th>
                        <th style={{ width: '10%' }}>Status</th>
                        <th style={{ width: '8%' }}>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {users.map((user) => (
                        <tr key={user._id}>
                          <td>
                            <div className="fw-bold">{user.name}</div>
                            <small className="text-muted">
                              Created: {new Date(user.createdAt).toLocaleDateString()}
                            </small>
                          </td>
                          <td>{user.email}</td>
                          <td>
                            <div className="text-truncate" style={{ maxWidth: '300px' }} title={user.address}>
                              {user.address}
                            </div>
                          </td>
                          <td>{getRoleBadge(user.role)}</td>
                          <td>
                            <Badge bg={user.isActive ? 'success' : 'danger'}>
                              {user.isActive ? 'Active' : 'Inactive'}
                            </Badge>
                          </td>
                          <td>
                            <div className="d-flex gap-1">
                              <Button
                                variant="outline-info"
                                size="sm"
                                onClick={() => fetchUserDetails(user._id)}
                                title="View Details"
                              >
                                <i className="bi bi-eye"></i>
                              </Button>
                              <Button
                                variant={user.isActive ? 'outline-danger' : 'outline-success'}
                                size="sm"
                                onClick={() => toggleUserStatus(user._id, user.isActive)}
                                title={user.isActive ? 'Deactivate' : 'Activate'}
                              >
                                <i className={`bi bi-${user.isActive ? 'x-circle' : 'check-circle'}`}></i>
                              </Button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card.Body>
            
            {/* Pagination */}
            {pagination.totalPages > 1 && (
              <Card.Footer className="bg-light">
                <div className="d-flex justify-content-between align-items-center">
                  <div className="text-muted small">
                    Showing page {pagination.currentPage} of {pagination.totalPages}
                  </div>
                  <div className="d-flex gap-2">
                    <Button
                      variant="outline-primary"
                      size="sm"
                      disabled={!pagination.hasPrevPage}
                      onClick={() => setCurrentPage(currentPage - 1)}
                    >
                      <i className="bi bi-chevron-left"></i> Previous
                    </Button>
                    <Button
                      variant="outline-primary"
                      size="sm"
                      disabled={!pagination.hasNextPage}
                      onClick={() => setCurrentPage(currentPage + 1)}
                    >
                      Next <i className="bi bi-chevron-right"></i>
                    </Button>
                  </div>
                </div>
              </Card.Footer>
            )}
          </Card>
        </Col>
      </Row>

      {/* Create User Modal */}
      <Modal show={showCreateModal} onHide={() => {
        setShowCreateModal(false);
        setFormErrors({});
      }} size="lg">
        <Modal.Header closeButton className="bg-primary text-white">
          <Modal.Title>
            <i className="bi bi-person-plus me-2"></i>
            Create New User
          </Modal.Title>
        </Modal.Header>
        <form onSubmit={handleCreateUser}>
          <Modal.Body>
            <Row>
              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Full Name <span className="text-danger">*</span></Form.Label>
                  <Form.Control
                    type="text"
                    value={createForm.name}
                    onChange={(e) => {
                      setCreateForm({...createForm, name: e.target.value});
                      if (formErrors.name) {
                        setFormErrors({...formErrors, name: undefined});
                      }
                    }}
                    placeholder="Enter full name (20-60 characters)"
                    isInvalid={!!formErrors.name}
                    minLength={20}
                    maxLength={60}
                  />
                  <Form.Control.Feedback type="invalid">
                    {formErrors.name}
                  </Form.Control.Feedback>
                  <Form.Text className="text-muted">
                    {createForm.name.length}/60 characters (minimum 20 required)
                  </Form.Text>
                </Form.Group>
              </Col>
            </Row>
            
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Email <span className="text-danger">*</span></Form.Label>
                  <Form.Control
                    type="email"
                    value={createForm.email}
                    onChange={(e) => {
                      setCreateForm({...createForm, email: e.target.value});
                      if (formErrors.email) {
                        setFormErrors({...formErrors, email: undefined});
                      }
                    }}
                    placeholder="Enter email address"
                    isInvalid={!!formErrors.email}
                  />
                  <Form.Control.Feedback type="invalid">
                    {formErrors.email}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Role <span className="text-danger">*</span></Form.Label>
                  <Form.Select
                    value={createForm.role}
                    onChange={(e) => setCreateForm({...createForm, role: e.target.value})}
                  >
                    <option value="user">Normal User</option>
                    <option value="store_owner">Store Owner</option>
                    <option value="admin">Admin</option>
                  </Form.Select>
                  <Form.Text className="text-muted">
                    {createForm.role === 'user' && 'Can browse and rate stores'}
                    {createForm.role === 'store_owner' && 'Can manage store ratings'}
                    {createForm.role === 'admin' && 'Full system access'}
                  </Form.Text>
                </Form.Group>
              </Col>
            </Row>
            
            <Row>
              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Password <span className="text-danger">*</span></Form.Label>
                  <Form.Control
                    type="password"
                    value={createForm.password}
                    onChange={(e) => {
                      setCreateForm({...createForm, password: e.target.value});
                      if (formErrors.password) {
                        setFormErrors({...formErrors, password: undefined});
                      }
                    }}
                    placeholder="Enter password (8-16 characters)"
                    isInvalid={!!formErrors.password}
                    minLength={8}
                    maxLength={16}
                  />
                  <Form.Control.Feedback type="invalid">
                    {formErrors.password}
                  </Form.Control.Feedback>
                 <Form.Text className="text-muted">
                   Must be 816 characters with at least one uppercase letter and one special character (!@#$%^&amp;*()_+-=[]{};':"&quot;\\|,.&lt;&gt;/?)
                   </Form.Text>
                </Form.Group>
              </Col>
            </Row>
            
            <Row>
              <Col>
                <Form.Group className="mb-3">
                  <Form.Label>Address <span className="text-danger">*</span></Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    value={createForm.address}
                    onChange={(e) => {
                      setCreateForm({...createForm, address: e.target.value});
                      if (formErrors.address) {
                        setFormErrors({...formErrors, address: undefined});
                      }
                    }}
                    placeholder="Enter address (max 400 characters)"
                    isInvalid={!!formErrors.address}
                    maxLength={400}
                  />
                  <Form.Control.Feedback type="invalid">
                    {formErrors.address}
                  </Form.Control.Feedback>
                  <Form.Text className="text-muted">
                    {createForm.address.length}/400 characters
                  </Form.Text>
                </Form.Group>
              </Col>
            </Row>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => {
              setShowCreateModal(false);
              setFormErrors({});
            }}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              <i className="bi bi-plus-circle me-2"></i>
              Create User
            </Button>
          </Modal.Footer>
        </form>
      </Modal>

      {/* User Details Modal */}
      <Modal show={showDetailsModal} onHide={() => setShowDetailsModal(false)} size="lg">
        <Modal.Header closeButton className="bg-info text-white">
          <Modal.Title>
            <i className="bi bi-person-circle me-2"></i>
            User Details
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedUser && (
            <div>
              <Row>
                <Col md={8}>
                  <h4 className="mb-3">{selectedUser.name}</h4>
                </Col>
                <Col md={4} className="text-end">
                  {getRoleBadge(selectedUser.role)}
                  <Badge bg={selectedUser.isActive ? 'success' : 'danger'} className="ms-2">
                    {selectedUser.isActive ? 'Active' : 'Inactive'}
                  </Badge>
                </Col>
              </Row>

              <hr />

              <Row className="mb-3">
                <Col md={6}>
                  <h6 className="text-muted mb-2">Email</h6>
                  <p className="mb-0">{selectedUser.email}</p>
                </Col>
                <Col md={6}>
                  <h6 className="text-muted mb-2">Role</h6>
                  <p className="mb-0">
                    {selectedUser.role === 'admin' && 'System Administrator'}
                    {selectedUser.role === 'user' && 'Normal User'}
                    {selectedUser.role === 'store_owner' && 'Store Owner'}
                  </p>
                </Col>
              </Row>

              <Row className="mb-3">
                <Col md={12}>
                  <h6 className="text-muted mb-2">Address</h6>
                  <p className="mb-0">{selectedUser.address}</p>
                </Col>
              </Row>

              <Row className="mb-3">
                <Col md={6}>
                  <h6 className="text-muted mb-2">Account Created</h6>
                  <p className="mb-0">{new Date(selectedUser.createdAt).toLocaleString()}</p>
                </Col>
                <Col md={6}>
                  <h6 className="text-muted mb-2">Last Updated</h6>
                  <p className="mb-0">{new Date(selectedUser.updatedAt).toLocaleString()}</p>
                </Col>
              </Row>

              {/* Store Owner Information */}
              {selectedUser.role === 'store_owner' && (
                <>
                  <hr />
                  <h5 className="mb-3">
                    <i className="bi bi-shop me-2"></i>
                    Store Information
                  </h5>
                  {userStoreInfo ? (
                    <Card className="bg-light">
                      <Card.Body>
                        <Row>
                          <Col md={8}>
                            <h6 className="mb-1">{userStoreInfo.name}</h6>
                            <p className="text-muted small mb-2">{userStoreInfo.email}</p>
                            <p className="text-muted small mb-0">
                              <i className="bi bi-geo-alt me-1"></i>
                              {userStoreInfo.address}
                            </p>
                          </Col>
                          <Col md={4} className="text-end">
                            <div className="mb-2">
                              <div className="h4 mb-0">
                                {renderStars(userStoreInfo.averageRating || 0)}
                              </div>
                              <div className="text-muted small">
                                {(userStoreInfo.averageRating || 0).toFixed(1)} / 5.0
                              </div>
                            </div>
                            <Badge bg="primary">
                              {userStoreInfo.totalRatings || 0} ratings
                            </Badge>
                          </Col>
                        </Row>
                      </Card.Body>
                    </Card>
                  ) : (
                    <Alert variant="warning">
                      <i className="bi bi-exclamation-triangle me-2"></i>
                      No store assigned to this store owner yet.
                    </Alert>
                  )}
                </>
              )}
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDetailsModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default AdminUsers;
